import React,{ useState } from "react";
import CompButton from "./Component/CompButton";
import CompTable from "./Component/CompTable";
import CompTiIn from "./Component/CompTiIn";
import './App.css';
import CompStatus from "./Component/CompStatus";



function App(){
  const [data, setData] = useState([]);
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");
  const [edit, setEdit] = useState({})
  const onChange = useState({})


  const handleNameChange = (event) =>{
     setName(event.target.value);
  }

 const handleAgeChange = (event) =>{
    setAge(event.target.value);
  }

const handleDelete = (index) => {
  const newData = [...data];
  newData.splice(index,1);
  setData(newData);
}

 const handleStatusChange = (value,index) => {
  setData([...data,{name: name, age: age, status: status }]);
  setName(name);
  setAge(age);
  setStatus(status);
  setError("");
 }

 const handleEdit = (item) => {
  setEdit(item);
 }

 const handleCancelEdit = () =>{
 setEdit({});
}


const handleSaveEdit = (index, newData) =>{
  const updatedData = [...data];
  updatedData[index] = newData;
  setEdit({});
  onChange(newData.name, index)
}

 const handleSubmit = () =>{
  if(!name || !age || !status){
    setError("Please fill complete input!");
    return
  }

     setData([...data,{name: name, age: age, status: status }]);
   setName("");
   setAge("");
   setStatus("");
   setError("");
 };

 return (
  <div className="myDiv">
     <h1>Form Data Input</h1>
     <CompTiIn 
       label="Name" 
       type="text"
       name="name"
       value={name}
       onChange={handleNameChange} />
       <br/>

     <br/>
     <CompTiIn
       label="Age"
       type="number"
       name="age"
       value={age}
       onChange={handleAgeChange}

            />
            <br/>
     <CompStatus
      title="Status"
      id="status"
       value={status}
      onChange={setStatus}                 />

                 <br/>
       {error && <p style={{color :"red"}} >{error}</p>}
       <CompButton onClick={handleSubmit}>Submit</CompButton>
      <CompTable data={data} 
      onEdit={handleStatusChange}
      onDelete={handleDelete}/><br/>
     </div>);}

     export default App
